package com.msg.gw.config;import org.springframework.beans.BeansException;import org.springframework.context.ApplicationContext;import org.springframework.context.ApplicationContextAware;import org.springframework.context.annotation.Configuration;@Configurationpublic class AppConfig implements ApplicationContextAware {			/**		 * Spring App Context var		 */	    private static ApplicationContext context;	    @Override	    public void setApplicationContext(ApplicationContext applicationContext) throws BeansException {	        context = applicationContext;	        String[] beanName = context.getBeanDefinitionNames();	        for(String bean:beanName) {	        	System.out.println(bean);	        }	    }	    public static ApplicationContext getContext() {	        return context;	    }}